import React, { Component } from 'react';
import {data} from './Data.js'
export class Item extends Component {
  constructor(props){
    super(props); 
    this.state ={
      items:[]
    }
  }
  refreshList(){
    fetch(data.API_URL+'pastila')
    .then(response =>response.json())
    .then(data=>{
      this.setState({items:data});
    });
  }
  componentDidMount(){
    this.refreshList();
  }
 
  render() {   
    const {
      items
    }=this.state;
    return (
      <div className='item'>
        <img src ={"./img/" + this.props.item.img} alt="ddd" onClick={() => this.props.onShowItem(this.props.item)}/>
        <h2>{this.props.item.title}</h2>
        <b>{this.props.item.price}₴</b>
        <b>{this.props.item.weight}</b>
        <div className='add-to-cart' onClick={() => this.props.addToOrder(this.props.item)}>+</div>
      </div>
    )
  }
}

export default Item
