import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      orders: [],
      items: [
        {
          id: 1,
          title: 'Пастила яблучна',
          img: 'apple.jpg',
          category: 'one',
          price: '55.00'
          },
          {
            id: 2,
            title: 'Пастила абрикосова',
            img: 'abrykos.jpg',
            category: 'one',
            price: '55.00'
            },
            {
              id: 3,
              title: 'Пастила абрикосово-яблучна',
              img: 'abrykos-apple.jpg',
              category: 'two',
              price: '75.55'
              },
              {
                id: 4,
                title: 'Пастила виноградно-яблучна',
                img: 'vyno-apple.jpg',
                category: 'two',
                price: '75.55'
                },
                {
                  id: 5,
                  title: 'Пастила гарбузова',
                  img: 'garb.jpg',
                  category: 'one',
                  price: '55.00'
                  },
                    {
                      id: 7,
                      title: 'Пастила з йошти',
                      img: 'yoshta.jpg',
                      category: 'one',
                      price: '99.99'
                      },
                      {
                        id: 8,
                        title: 'Пастила малинова',
                        img: 'malyna.jpg',
                        category: 'one',
                        price: '55.00'
                        },
                          {
                            id: 10,
                            title: 'Пастила смородинова',
                            img: 'smorod.jpg',
                            category: 'one',
                            price: '55.00'
                            },
                            {
                              id: 11,
                              title: 'Пастила смородиново-яблучна',
                              img: 'smorod-apple.jpg',
                              category: 'two',
                              price: '75.55'
                              },

      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
    this.chooseCategory = this.chooseCategory.bind(this)
  }
  render(){
  return (
   <div className="wrapper">
    <Header orders = {this.state.orders} deleteOrder={this.deleteOrder}/>
    <Categories chooseCategory={this.chooseCategory}/>
    <Items items ={this.state.items} addToOrder={this.addToOrder}/>
    <Footer/>
   </div>
  )
  }

  chooseCategory (category){
    console.log(category);
  }

  deleteOrder(id){
    this.setState({orders: this.state.orders.filter(el => el.id !== id)})
  }

  addToOrder(item){
    let isInArray = false
    this.state.orders.forEach(el => {
      if(el.id === item.id)
      isInArray = true
    })
    if(!isInArray)
    this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
