import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      orders: [],
      items: [
        {
          id: 1,
          title: 'Пастила яблучна',
          img: 'apple.jpg',
          price: '55.00'
          },
          {
            id: 2,
            title: 'Пастила абрикосова',
            img: 'abrykos.jpg',
            price: '55.00'
            },
            {
              id: 3,
              title: 'Пастила абрикосово-яблучна',
              img: 'abrykos-apple.jpg',
              price: '55.00'
              },
              {
                id: 4,
                title: 'Пастила виноградно-яблучна',
                img: 'vyno-apple.jpg',
                price: '55.00'
                },
                {
                  id: 5,
                  title: 'Пастила гарбузова',
                  img: 'garb.jpg',
                  price: '55.00'
                  },
                  {
                    id: 6,
                    title: 'Пастила гарбузово-яблучна',
                    img: 'garb-apple.jpg',
                    price: '55.00'
                    },
                    {
                      id: 7,
                      title: 'Пастила з йошти',
                      img: 'yoshta.jpg',
                      price: '55.00'
                      },
                      {
                        id: 8,
                        title: 'Пастила малинова',
                        img: 'malyna.jpg',
                        price: '55.00'
                        },
                        {
                          id: 9,
                          title: 'Пастила малиново-яблучна',
                          img: 'malyna-apple.jpg',
                          price: '55.00'
                          },
                          {
                            id: 10,
                            title: 'Пастила смородинова',
                            img: 'smorod.jpg',
                            price: '55.00'
                            },
                            {
                              id: 11,
                              title: 'Пастила смородиново-яблучна',
                              img: 'smorod-apple.jpg',
                              price: '55.00'
                              },

      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }
  render(){
  return (
   <div className="wrapper">
    <Header orders = {this.state.orders}/>
    <Items items ={this.state.items} addToOrder={this.addToOrder}/>
    <Footer/>
   </div>
  )
  }

  addToOrder(item){
    let isInArray = false
    this.state.orders.forEach(el => {
      if(el.id === item.id)
      isInArray = true
    })
    if(!isInArray)
    this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
