import React, { Component } from 'react';
import {data} from './Data.js'
export class Item extends Component {
  constructor(props){
    super(props); 
    this.state ={
      items:[]
    }
  }

  componentDidMount(){
    this.refreshList();
  }
 
  render() {   
    const {
      items
    }=this.state;
    return (
      <div className='item'>
        <img src ={"./img/" + this.props.items.imageFileName} alt="ddd" onClick={() => this.props.onShowItem(this.props.item)}/>
        <h2>{this.props.items.title}</h2>
        <b>{this.props.items.price}₴</b>
        <b>{this.props.items.weight}</b>
        <div className='add-to-cart' onClick={() => this.props.addToOrder(this.props.item)}>+</div>
      </div>
    )
  }
}

export default Item
